import { useCallback, useState } from 'react';
import PropTypes from 'prop-types';

const PostImages = ({ images }) => {
  const [showImagesZoom, setShowImagesZoom] = useState(false);

  const onZoom = useCallback(() => {
    setShowImagesZoom(true);
  }, []);

  if (images.length === 3) {
    return (
      <>
        <img
          role="presentation"
          style={{
            maxHeight: 200,
          }}
          src={images[0].src}
          alt={images[0].src}
          onCLick={onZoom}
        />
      </>
    );
  }

  if (images.length === 2) {
    return (
      <>
        <img
          role="presentation"
          style={{ width: '50%' }}
          src={images[0].src}
          alt={images[0].src}
          onCLick={onZoom}
        />
        <img
          role="presentation"
          style={{ width: '50%' }}
          src={images[1].src}
          alt={images[1].src}
          onCLick={onZoom}
        />
      </>
    );
  }

  return (
    <>
      <div>
        <img
          role="presentation"
          src={images[0].src}
          alt={images[0].src}
          width="50%"
          onClick={onZoom}
        />
        <div
          role="presentation"
          style={{
            display: 'inline-block',
            width: '50%',
            textAlign: 'center',
            verticalAlign: 'middle',
          }}
          onClick={onZoom}
        >
          {/* <PlusOutlined /> */}
          <br />
          {images.length - 1}
          개의 사진 더보기
        </div>
      </div>
      {showImagesZoom && <ImagesZoom images={images} onClose={onClose} />}
    </>
  );
};

PostImages.propTypes = {
  images: PropTypes.arrayOf(PropTypes.object),
};

export default PostImages;
